// Smart JSON Cleaner 3.0
// Cleans AI Markdown safely for JSON while preserving real newlines

const input = $input.item.json;
let text = input?.output?.output ?? '';
// let text = $input.first().json.output;

// Ensure string type
text = String(text);

// STEP 1: Normalize escape sequences
text = text
  .replace(/\\r\\n/g, '\n')  // Replace escaped CRLF -> real newline
  .replace(/\\n/g, '\n')     // Replace escaped LF -> real newline
  .replace(/\\r/g, '\n')     // Replace escaped CR -> real newline
  .replace(/\\t/g, ' ')      // Replace escaped tabs -> space
  .replace(/\\b|\\f/g, ' ')  // Remove other escapes
  .replace(/\u0000/g, ' ');  // Remove null bytes

// STEP 2: Remove control chars except newlines
text = text.replace(/[\u0001-\u0009\u000B-\u001F\u007F]/g, ' ');

// STEP 3: Fix unescaped quotes (replace double quotes with single)
text = text.replace(/"/g, "'");

// STEP 4: Remove unnecessary backslashes, but keep valid ones for URLs
text = text.replace(/(\\)(?![\\\/ntrfb"])/g, '');

// STEP 5: Collapse extra spaces but KEEP newlines
text = text
  .replace(/[ \t]+/g, ' ')      // collapse multiple spaces
  .replace(/\n{3,}/g, '\n\n')   // more than 2 newlines → 2 newlines
  .trim();

// STEP 6: Validate JSON safety
try {
  JSON.stringify({ output: text });
} catch (err) {
  text = text.replace(/'/g, '');
}

// Return clean and readable Markdown JSON
return [{ json: { output: text } }];

